void* mTHA_AllocEnd(TwoHeadArena* tha, u32 size) 
{
    u32 mask;

    if (size == 8) {
        mask = ~7;
    } else if (size == 4 || size == 12) {
        mask = ~3;
    } else if (size == 2 || size == 6 || size == 10 || size == 12 || size == 14) {
        mask = ~1;
    } else {
        mask = (size >= 0x10) ? ~0xF : 0;
    }

    tha->tail = (void*)((((u32)tha->tail & mask) - size) & mask);
    return tha->tail;
}

Gfx* mTHGA_AllocEnd(TwoHeadGfxArena* thga, u32 size) 
{
    return mTHA_AllocEnd((TwoHeadArena*)thga, size);
}

void* mGraph_Alloc(GraphicsContext* gfxCtx, size_t size) 
{
    return mTHGA_AllocEnd(&gfxCtx->polyOpa, ALIGN16(size));
}

