#define TEXT_SCALE 75
#define NES_FONT_STATIC 0x928000

typedef struct 
{
	u8 charTexBuf[FONT_CHAR_TEX_SIZE * 10];
    s16 CurAlpha;
    s16 CurAlphaTextbox;
    u8 SpeakerLastFrame;
} UIStruct;

#define UIData ((UIStruct*)npc->scriptVars[0])
#define CurSpeaker npc->scriptVars[1]

void DrawCharTexture(Gfx** gfxp, u8* texture, s32 x, s32 y, int lowercaseOffset) 
{
    Gfx* gfx = *gfxp;

    s32 sCharTexScale = 1024.0f / (TEXT_SCALE / 100.0f);
    s32 sCharTexSize = (TEXT_SCALE / 100.0f) * 16.0f;

    gDPLoadTextureBlock_4b(gfx++, texture, G_IM_FMT_I, 16, 16, 0, G_TX_NOMIRROR | G_TX_CLAMP, G_TX_NOMIRROR | G_TX_CLAMP, 
                        G_TX_NOMASK, G_TX_NOMASK, G_TX_NOLOD, G_TX_NOLOD);

    gSPTextureRectangle(gfx++, x << 2, (y << 2) + lowercaseOffset, (x + sCharTexSize) << 2, ((y + sCharTexSize) << 2) + lowercaseOffset,
                        G_TX_RENDERTILE, 0, 0, sCharTexScale, sCharTexScale);

    *gfxp = gfx;
}

int GetTextLen(char* Mes)
{
    int Len = 0;
    for (int i = 0; i < strlen(Mes); i++) 
        Len += (s32)(fontWidths[Mes[i] - 0x20] * (TEXT_SCALE / 100.0f));

    return Len;
}

void LoadSpeakerChar(UIStruct* d, u8 character, u16 codePointIndex) 
{
    DmaMgr_SendRequest1(&d->charTexBuf[codePointIndex], NES_FONT_STATIC + (character * FONT_CHAR_TEX_SIZE), FONT_CHAR_TEX_SIZE);
}

void DrawText(NpcMaker* npc, PlayState* playState, Gfx** gfxp, Color_RGB8 Color, Color_RGB8 ShadowColor, char* Name, s32* XPos, s32* YPos)
{
    Gfx* gfx = *gfxp;
    u16 i;
            
	Gfx_SetupDL_39Ptr(&gfx);
    gDPSetAlphaCompare(gfx++, G_AC_NONE);
    gDPSetCombineMode(gfx++, G_CC_MODULATEIA_PRIM, G_CC_MODULATEIA_PRIM);
	
    int textLen = strlen(Name);
	
	u32 charIdx = 0;
	Lib_MemSet((void*)&UIData->charTexBuf, 10 * FONT_CHAR_TEX_SIZE, 0);
	

    for (i = 0; i < textLen; i++) 
    {		
        if (Name[i] == ' ') 
        {
            *XPos += 6;
            continue;
        }

		LoadSpeakerChar(UIData, Name[i]  - ' ', charIdx);
		gDPPipeSync(gfx++);
		gDPSetCombineLERP(gfx++, 0, 0, 0, PRIMITIVE, TEXEL0, 0, PRIMITIVE, 0, 0, 0, 0, PRIMITIVE, TEXEL0, 0, PRIMITIVE, 0);
		gDPSetPrimColor(gfx++, 0, 0, ShadowColor.r, ShadowColor.g, ShadowColor.b, UIData->CurAlpha);
		DrawCharTexture(&gfx, &UIData->charTexBuf[charIdx], *XPos, *YPos + 1, 0);
	
		gDPPipeSync(gfx++);
		gDPSetCombineLERP(gfx++, 0, 0, 0, PRIMITIVE, TEXEL0, 0, PRIMITIVE, 0, 0, 0, 0, PRIMITIVE, TEXEL0, 0, PRIMITIVE, 0);
		gDPSetPrimColor(gfx++, 0, 0, Color.r, Color.g, Color.b, UIData->CurAlpha);
        DrawCharTexture(&gfx, &UIData->charTexBuf[charIdx], *XPos, *YPos, 0);
		
        *XPos += (s32)(fontWidths[Name[i] - ' '] * (TEXT_SCALE / 100.0f));
		
		charIdx += FONT_CHAR_TEX_SIZE;
    }

    *gfxp = gfx;
}

void DrawInner(NpcMaker* npc, PlayState* playState, Gfx** gfxp) 
{
    Gfx* gfx = *gfxp;

    Gfx_SetupDL_56Ptr(&gfx);
    Gfx_SetupDL_39Ptr(&gfx);
    gDPPipeSync(gfx++);


    int rTextboxTex = 4096;
    int rTextboxWidth = 64;
    int rTextboxHeight = 16;
    int rTextboxX = VREG(0) + 11;
    int rTextboxY = 0;
    
    if (VREG(1) < 128)
        rTextboxY = VREG(1) + 64;
    else
        rTextboxY = VREG(1) - 18;


    Color_RGB8 txtBoxColor = textboxColors[CurSpeaker];
    gDPSetPrimColor(gfx++, 0, 0, txtBoxColor.r, txtBoxColor.g, txtBoxColor.b, UIData->CurAlphaTextbox);

    gDPLoadTextureBlock_4b(gfx++, playState->msgCtx.textboxSegment, G_IM_FMT_I, 128, 64, 0, G_TX_MIRROR, G_TX_NOMIRROR, 7, 0,
                                G_TX_NOLOD, G_TX_NOLOD);

    gSPTextureRectangle(gfx++, rTextboxX << 2, rTextboxY << 2, (rTextboxX + rTextboxWidth) << 2,
                        (rTextboxY + rTextboxHeight) << 2, G_TX_RENDERTILE, 0, 0, rTextboxTex, rTextboxTex);

    *gfxp = gfx;
	
	
	if (UIData->SpeakerLastFrame != CurSpeaker)
		return;

    s32 TEXT_POS_X = rTextboxX + ((rTextboxWidth - GetTextLen(speakerNames[CurSpeaker])) / 2);
    s32 TEXT_POS_Y = rTextboxY + rTextboxHeight - 14;
    DrawText(npc, playState, gfxp, textColors[CurSpeaker], textShadowColors[CurSpeaker], speakerNames[CurSpeaker], &TEXT_POS_X, &TEXT_POS_Y);
}


void NpcM_UI_Init(NpcMaker* npc, PlayState* playState)
{
    npc->scriptVars[0] = (u32)ZeldaArena_Malloc(sizeof(UIStruct));
    
    CurSpeaker = 255;
    UIData->CurAlpha = 0;
    UIData->CurAlphaTextbox = 0;   
}

void NpcM_UI_Draw(NpcMaker* npc, PlayState* playState)
{
    Gfx* gfx;
    Gfx* gfxRef;
	
	// Set textbox pos
	u32* cutsceneBarSize = (u32*)0x800FE478;
		
	// If message box pos is top...
	if (playState->msgCtx.textBoxPos == 2)
		VREG(1) = 18 + *cutsceneBarSize;
	else
		VREG(1) = 168 - *cutsceneBarSize;

	// Continue arrow pos
	XREG(65) = VREG(1) + 59;


	GraphicsContext* __gfxCtx = playState->state.gfxCtx;

    gfxRef = POLY_OPA_DISP;
    gfx = Graph_GfxPlusOne(gfxRef);
    gSPDisplayList(OVERLAY_DISP++, gfx);

    switch (playState->msgCtx.msgMode)
    {
        case MSGMODE_NONE: 
        case MSGMODE_TEXT_START: 
        case MSGMODE_TEXT_BOX_GROWING: 
        case MSGMODE_TEXT_STARTING: 
            {
                Math_SmoothStepToS(&UIData->CurAlphaTextbox, 0, 2, 60, 5);
                Math_SmoothStepToS(&UIData->CurAlpha, 0, 2, 60, 5);
                break;
            }
        default:
            {
                Math_SmoothStepToS(&UIData->CurAlphaTextbox, 200, 1, 80, 5);
                Math_SmoothStepToS(&UIData->CurAlpha, 255, 1, 80, 5);
                break;
            }
    }

    if (CurSpeaker <= 8)
        DrawInner(npc, playState, &gfx);

    gSPEndDisplayList(gfx++);
    Graph_BranchDlist(gfxRef, gfx);
    POLY_OPA_DISP = gfx;
	
	UIData->SpeakerLastFrame = CurSpeaker;
}

void NpcM_UI_Update(NpcMaker* npc, PlayState* playState)
{
    // Shake screen whenever explosion sound is used.
	u32* TextSound = (u32*)0x80114B58;
	
	if (*TextSound == 0x180E)
	{
        s32 temp = Quake_Add(GET_ACTIVE_CAM(playState), 1);
        Quake_SetSpeed(temp, 0x3A98);
        Quake_SetQuakeValues(temp, 0, 1, 0xFA, 1);
        Quake_SetCountdown(temp, 0xA);
    }    
}

void NpcM_UI_Delete(NpcMaker* npc, PlayState* PlayState)
{
    ZeldaArena_Free(UIData);
}
