NpcMaker* Scene_GetNpcMakerByID(NpcMaker* en, PlayState* playState, u16 ID)
{
    NpcMaker* npc = (NpcMaker*)playState->actorCtx.actorLists[ACTORCAT_NPC].head;

    while (npc)
    {
        if (npc->actor.id == en->actor.id)
        {
            if (npc->npcId == ID)
                break;
        }

        npc = (NpcMaker*)npc->actor.next;
    }

    return npc;
}

float NpcM_AnimTest(NpcMaker* npc, PlayState* playState)
{
    NpcMaker* npc2 = Scene_GetNpcMakerByID(npc, playState, npc->scriptVars[1]);


    Gfx* gfx = Graph_GfxPlusOne(playState->state.gfxCtx->polyOpa.p);
    gSPDisplayList(playState->state.gfxCtx->overlay.p++, gfx);
    GfxPrint printer;

    GfxPrint_Init(&printer);
    GfxPrint_Open(&printer, gfx);
    GfxPrint_SetColor(&printer, 255, 255, 255, 255);

    GfxPrint_SetPos(&printer, 2, 2);

    GfxPrint_Printf(&printer, "RIGHT/LEFT - Change Animation");

    GfxPrint_SetPos(&printer, 2, 3);
    
    GfxPrint_Printf(&printer, "Z - Go back");

    GfxPrint_SetPos(&printer, 2, 8);

    GfxPrint_Printf(&printer, "Anim ID [%02d]", npc->scriptVars[4]);


    GfxPrint_SetPos(&printer, 2, 9);

    GfxPrint_Printf(&printer, "Anim Offs [%08x]", npc2->animations[npc->scriptVars[4]].offset);


    float ret = -1;


    gfx = GfxPrint_Close(&printer);
    GfxPrint_Destroy(&printer);
    gSPEndDisplayList(gfx++);
    Graph_BranchDlist(playState->state.gfxCtx->polyOpa.p, gfx);
    playState->state.gfxCtx->polyOpa.p = gfx;


    if (CHECK_BTN_ALL(playState->state.input->press.button, BTN_DRIGHT))
    {
        npc->scriptVars[4]++;
        
        if (npc2->numAnims <= npc->scriptVars[4])
            npc->scriptVars[4] = 0;

        while (!npc2->animations[npc->scriptVars[4]].offset)
        {
            npc->scriptVars[4]++;

            if (npc2->numAnims <= npc->scriptVars[4])
            {
                npc->scriptVars[4] = 0;
                break;
            }
        }

        ret = 1;
    }

    if (CHECK_BTN_ALL(playState->state.input->press.button, BTN_DLEFT))
    {
        npc->scriptVars[4]--;
        
        if (npc->scriptVars[4] < 0)
            npc->scriptVars[4] = npc2->numAnims - 1;

        while (!npc2->animations[npc->scriptVars[4]].offset)
        {
            npc->scriptVars[4]--;

            if (npc->scriptVars[4] < 0)
            {
                npc->scriptVars[4] = npc2->numAnims - 1;
                break;
            }
        }       

        ret = 1;
    }  
    if (CHECK_BTN_ALL(playState->state.input->press.button, BTN_Z))
        ret = 2;

    return ret;
}


float NpcM_CharSelect(NpcMaker* npc, PlayState* playState)
{
    Gfx* gfx = Graph_GfxPlusOne(playState->state.gfxCtx->polyOpa.p);
    gSPDisplayList(playState->state.gfxCtx->overlay.p++, gfx);
    GfxPrint printer;

    GfxPrint_Init(&printer);
    GfxPrint_Open(&printer, gfx);
    GfxPrint_SetColor(&printer, 255, 255, 255, 255);

    GfxPrint_SetPos(&printer, TITLE_X, TITLE_Y);

    GfxPrint_Printf(&printer, "ANIMATION / SCENE ANGLE TEST");

    for (int i = 0; i < MAX_SCENE_SELECT_ENTRIES; i++)
    {
        GfxPrint_SetPos(&printer, LIST_X, LIST_Y + i);
        GfxPrint_Printf(&printer, sceneNames[(int)FirstSceneIdx + i]);
    }

    GfxPrint_SetPos(&printer, CURSOR_X, LIST_Y + CursorCurPos);
    GfxPrint_Printf(&printer, ">");

    int ret = -1;

    if (Cooldown)
    {
        Cooldown--;
        goto end;
    }

    if (CHECK_BTN_ALL(playState->state.input->press.button, BTN_DDOWN))
    {
        CursorCurPos++;
        Cooldown = COOLDOWN_TIMER;

        if (CursorCurPos > MAX_SCENE_SELECT_ENTRIES - 1)
        {
            FirstSceneIdx++;
            CursorCurPos = MAX_SCENE_SELECT_ENTRIES - 1;
        }
    }

    if (CHECK_BTN_ALL(playState->state.input->press.button, BTN_DUP))
    {
        CursorCurPos--;
        Cooldown = COOLDOWN_TIMER;

        if (CursorCurPos < 0)
        {
            FirstSceneIdx--;
            CursorCurPos = 0;

            if (FirstSceneIdx + MAX_SCENE_SELECT_ENTRIES > ARRAY_COUNT(sceneNames))
                FirstSceneIdx--;
        }
    }

    if (CHECK_BTN_ALL(playState->state.input->press.button, BTN_DRIGHT))
    {
        CursorCurPos += 10;
        Cooldown = COOLDOWN_TIMER;

        if (CursorCurPos > MAX_SCENE_SELECT_ENTRIES - 1)
        {
            FirstSceneIdx += MAX_SCENE_SELECT_ENTRIES - CursorCurPos;
            CursorCurPos = MAX_SCENE_SELECT_ENTRIES - 1;
        }

    }

    if (CHECK_BTN_ALL(playState->state.input->press.button, BTN_DLEFT))
    {
        CursorCurPos -= 10;
        Cooldown = COOLDOWN_TIMER;

        if (CursorCurPos < 0)
        {
            FirstSceneIdx -=  CursorCurPos;
            CursorCurPos = 0;
        }

    }   

    if (FirstSceneIdx + MAX_SCENE_SELECT_ENTRIES > ARRAY_COUNT(sceneNames))
    {
        CursorCurPos = 0;
        FirstSceneIdx = 0;
    }

    if (FirstSceneIdx < 0)
    {
        CursorCurPos = MAX_SCENE_SELECT_ENTRIES - 1;
        FirstSceneIdx = ARRAY_COUNT(sceneNames) - MAX_SCENE_SELECT_ENTRIES;
    }

    if (CHECK_BTN_ALL(playState->state.input->press.button, BTN_A))
    {
        int ScenarioNum = (int)(FirstSceneIdx + CursorCurPos);
        ret = CursorCurPos;
    }
        

end:
    gfx = GfxPrint_Close(&printer);
    GfxPrint_Destroy(&printer);
    gSPEndDisplayList(gfx++);
    Graph_BranchDlist(playState->state.gfxCtx->polyOpa.p, gfx);
    playState->state.gfxCtx->polyOpa.p = gfx;


    npc->scriptVars[1] = NpcsIdx[CursorCurPos];

    return ret;



}
