ThinkingEntry *GetThinkingEntry(int id)
{
    for (int i = 0; i < ARRAY_COUNT(SeqThinking); i++)
    {
        if (SeqThinking[i].seqId == id)
            return &SeqThinking[i];
    }

    return NULL;
}

void FadeMusicChannels(NpcMaker* npc, ThinkingEntryTrack *tracks, bool target, bool doFade)
{
    u8 duration = doFade ? (target ? THINKING_FADEIN_DURATION : THINKING_FADEOUT_DURATION) : 5;
    for (int j = 0; j < 16; j++)
    {
        SEQCMD_SET_CHANNEL_VOLUME(
            SEQ_PLAYER_BGM_MAIN,
            j,
            duration,
            tracks ? (target ? tracks[j].volumeNormal : tracks[j].volumeThinking) : 127
        );
    }
}

// Monitor if new music is switched in and set their initial volumes.
void NpcM_CheckIfNewMusicIdAndFade(NpcMaker* npc, PlayState* playState)
{
    int curSeq = func_800FA0B4(SEQ_PLAYER_BGM_MAIN);

    if (curSeq != THINKING_LAST_MUSIC)
    {
        ThinkingEntry *entry = GetThinkingEntry(curSeq);
        if (entry != NULL)
        {
            ThinkingEntryTrack *tracks = &entry->tracks[0];
            FadeMusicChannels(npc, tracks, MUSIC_MODE_NORMAL, false);
        }
    }

    THINKING_LAST_MUSIC = curSeq;
}

// Fade out channels marked for fading out, and fade in channels marked for fade-in.
void NpcM_FadeIntoThinkingMusic(NpcMaker* npc, PlayState* playState)
{
    int curSeq = func_800FA0B4(SEQ_PLAYER_BGM_MAIN);

    ThinkingEntry *entry = GetThinkingEntry(curSeq);
    if (entry != NULL)
        FadeMusicChannels(npc, &entry->tracks[0], MUSIC_MODE_THINKING, true);
}

// Fade in channels marked for fading out, and fade out channels marked for fade-in.
void NpcM_FadeOutOfThinkingMusic(NpcMaker* npc, PlayState* playState)
{
    int curSeq = func_800FA0B4(SEQ_PLAYER_BGM_MAIN);

    ThinkingEntry *entry = GetThinkingEntry(curSeq);
    if (entry != NULL)
        FadeMusicChannels(npc, &entry->tracks[0], MUSIC_MODE_NORMAL, true);
    else
        FadeMusicChannels(npc, NULL, MUSIC_MODE_NORMAL, true);
}


